		p+=tx[i];
		p-=ty[i];
		ans=max(ans,p);//求出最大值 
	}
	if(ans%36!=0) printf("%d",ans/36+1);//莫名其妙ceil挂了，凑活一下吧 
	else printf("%d",ans/36);
