	int len,si;
	cin>>len;
	for(int o=0;o<len;o++){
		for(int p=0;p<len;p++){
			cin>>zy[o][p];
		}
	}
	for(int u=0;u<len;u++){
		for(int v=0;v<len;v++){
			cin>>zf[u][v];
		}
	}
	for(si=1;si<=4;si++){
		for(int a=0;a<len;a++){
		    for(int b=0;b<len;b++){
			    if(zy[a][b]=='O') cout<<zf[a][b];
		    }
	    }
	    swaps(zy,len);
	}
	return 0;
}
```

# 题目中说是旋转90度，通过样例，我们可以得知，是顺时针旋转 

附上代码：

```cpp
#include<map>
#include<list>
#include<cmath>
#include<cstdio>
#include<cstring>
#include<iostream>
#include<algorithm>
using namespace std;
char a[1001][1001],b[1001][1001],c[1001][1001];//a是读入的#和O，b是密钥，c是旋转后的 
int n;//情书大小 
inline void gg(int x){
	if(x%2==0){//双数就用c来存a，单数a存c，节约空间 
		for(int i=1;i<=n;i++)
			for(int j=1;j<=n;j++)
				c[j][n-i+1]=a[i][j];//将其逆时针旋转，最开始转反了，如果写成c[i][j]=a[j][n-i+1]是逆时针转 
		for(int i=1;i<=n;i++)
			for(int j=1;j<=n;j++)
				if(c[i][j]=='O') printf("%c",b[i][j]);//是O就输出这里的b（最开始不知为啥经常不输出） 
		return;//不想写else（等价） 
	}
	for(int i=1;i<=n;i++)
		for(int j=1;j<=n;j++)
			a[j][n-i+1]=c[i][j];//反过来存上一次变化后的 
	for(int i=1;i<=n;i++)
		for(int j=1;j<=n;j++)
			if(a[i][j]=='O') printf("%c",b[i][j]);//是O就输出这里的b				
