	else return a.x<b.x;
}

void addnode(int x,int y)
{
	s[++cnt].x=x;
	s[cnt].y=y;
}

void rotate()
{
	for(int i=1;i<=cnt;i++)//旋转的坐标更改
	{
		int x=s[i].x;
		int y=s[i].y;
		s[i].y=n-x+1;
		s[i].x=y;
