    n = read(), r = read(), g = read(), b = read(), T = read();
    for (int i = 1; i <= n; i++)
        for (int j = 1; j <= i; j++)
            f[i][j] = max(j < i ? f[i - 1][j] + ((i - 1 - j) * b + T) * (g * j) : 0, f[i - 1][j - 1] + ((i - j) * b + T) * (g * (j - 1)));
    for (int i = 0; i <= n; i++)
        for (int j = 0; j <= i; j++)
            ans = max(ans, f[i][j] + (n - i) * ((i - j) * b + T) * (r + g * j));
    print(ans);
    return 0;
}
