            stck_1[++top_1]=x;
            sum[top_1]=sum[top_1-1]+x;
            f[top_1]=max(f[top_1-1],sum[top_1]);
        }else if(opt=='Q'){//查询最大前缀和
            int x;
            scanf("%d",&x);
            printf("%d\n",f[x]);
        }else if(opt=='L'){//光标左移
            if(!top_1)continue;
            stck_2[++top_2]=stck_1[top_1--];
        }else if(opt=='R'){//光标右移
            if(!top_2)continue;
            stck_1[++top_1]=stck_2[top_2--];
            sum[top_1]=sum[top_1-1]+stck_1[top_1];
            f[top_1]=max(f[top_1-1],sum[top_1]);
        }else if(opt=='D'){//删除光标前一个元素
            if(!top_1)continue;
            top_1--;
        }
    }
