	if(dfs(now+1,minsx_,maxsx_,minsy_,maxsy_))
	{
		for(int i=1;i<=n;i++)
		{
			b[i]=b_[i];
		}
		return 1;
	}
	for(int i=1;i<=n;i++)
	{
		b[i]=b_[i];
	}
	minsx_=0x7fffffff,maxsx_=-0x7fffffff,minsy_=0x7fffffff,maxsy_=-0x7fffffff;
	for(int i=1;i<=n;i++)
	{
		if(x[i]>=maxsx-L&&y[i]<=minsy+L)
		{
			b[i]=1;
		}
		if(!b[i])
		{
			minsx_=min(minsx_,x[i]);
