    num.clear();
    ans.clear();
    num.m=num.n=8;//num用于进行矩阵快速幂
    num.a[1][2]=1;
    num.a[1][8]=1;
    num.a[2][1]=1;
    num.a[2][3]=1;
    num.a[3][2]=1;
    num.a[3][4]=1;
    num.a[4][3]=1;
    num.a[4][5]=1;
    num.a[6][5]=1;
    num.a[6][7]=1;
    num.a[7][6]=1;
    num.a[7][8]=1;
    num.a[8][7]=1;
    num.a[8][1]=1;
