using namespace fastio;
const int mod=1000;
struct num{
	int a,b;
	num operator *(const num x)const{
		num ret;
		ret.b=(2*x.a*a+x.b*b)%mod;
		ret.a=(x.a*b+x.b*a)%mod; 
		return ret;
	}
	num operator +(const num x)const{
		num ret;
		ret.a=(x.a+a)%mod;
		ret.b=(x.b+b)%mod; 
		return ret;
	}
};
int n;
num poww(num a,int b){
	num ans=(num){0,1};
	while(b){
		if(b&1) ans=ans*a;
		a=a*a;
		b>>=1; 
	}
	return ans;
}
