int main(){
	n=read();
	dp[0][1]=1;
	for(int i=1;i<=n;i++){
          dp[now][1]=dp[now^1][8]+dp[now^1][2];
          dp[now][2]=dp[now^1][3]+dp[now^1][1];
          dp[now][3]=dp[now^1][4]+dp[now^1][2];
          dp[now][4]=dp[now^1][3];
          dp[now][5]=dp[now^1][4]+dp[now^1][6];
          dp[now][6]=dp[now^1][7];
          dp[now][7]=dp[now^1][6]+dp[now^1][8];
