	while(ch>'9'||ch<'0') {if(ch == '-') f_f = -1; ch = getchar();}
	while(ch>='0'&&ch<='9') red = red*10+ch-'0', ch = getchar();
	return red * f_f;
}

int main()
{
	n = read(), x = read(), y = read();
	int m = min(x, min(y, min(n-x+1, n-y+1))), lt = 1;
    //m表是要求的在第m层
	for(int i = 1; i < m; i++)	//lt 表示距离
    	lt += (n-i*2+1)*4;
	if(x == m || y == n-m+1)	//上面的
    	ans = lt + x-m + y-m;
	else						//下面的
    	ans = lt + (n-(m-1)*2-1)*4-1 - (x-m + y-m)+1;
	printf("%d\n", ans);
	return 0;
}
