```cpp
#include<cstdio>
#include<string>
#include<iostream>
#include<algorithm>
#include<vector>
using namespace std;
int n,x,y,start=1;
void solve() {
    if (x==1) {//在上面的边上
        cout<<start+y-1<<endl;
        exit(0);
    }
    else if (y==n) {//右面
        cout<<n+x-2+start<<endl;
        exit(0);
    }
    else if (x==n) {//下面
        cout<<3*n-2-y+start<<endl;
        exit(0);
    }
    else if (y==1) {//左面
        cout<<4*n-3-x+start<<endl;
        exit(0);
    }
    else {
        start+=(n-1)*4;//更新起点的数字
        x--,y--;//新的坐标
        n-=2;//新的大小
        solve();
    }
}
int main() {
   cin>>n>>x>>y;
    solve();
    return 0;
}

