{
    cin>>n>>i>>j;
    if(i==1)//一开始的n个数肯定都在第一行，所以当i=1时直接输出j即可
    {
        cout<<j;
        return 0;
    }
    int head=n-1,x1=1,y1=n,x2,y2,d=1,v=n;//x1，y1用来表示起点坐标，x2，y2表示终点，v表示每次移动完后末尾那个点的数字的值。
    while(head>=1)//每次循环移动操作两次
    {
        x2=x1+dx[d]*head;
        y2=y1+dy[d]*head;
        v+=head;
        if(i==x1&&i==x2)//i在该行时
        {
            ans=v-abs(y2-j);//一定要用绝对值！！我在这里被坑了好久qwq
            cout<<ans;
