        if(j==y1&&j==y2)//j在该列时
        {
            ans=v-abs(x2-i);
            cout<<ans;
            return 0;
        }
        d=(d+1)%4;//调整方向，下同。
        x1=x2;//更新起点。
        y1=y2;
        x2=x1+dx[d]*head;
        y2=y1+dy[d]*head;
        v+=head;
        if(i==x1&&i==x2)
        {
            ans=v-abs(y2-j);
            cout<<ans;
            return 0;
        }
        if(j==y1&&j==y2)
        {
            ans=v-abs(x2-i);
            cout<<ans;
            return 0;
        }
        d=(d+1)%4;
        x1=x2;
        y1=y2;
        head--;//head功能见上述规律
    }
}
