#include<cstdio>
long long M,N,x,y;
long long min(int a, int b) {
    return a<b?a:b;
}
int main() {
    scanf("%lld%lld",&M,&N);
    x=((M+1)*M/2)*((N+1)*N/2);
    for(long long i=1; i<= min(M,N); i++) {
