  wb=record
   n:^wb;
   t:longint;
  end;
  pwb=^wb;
  we=record
   i,d:longint;
  end;
  wa=array of we;
  cmp=object
   public
    function c(x,y:we):boolean;inline;static;
   end;
  sort=specialize Torderingarrayutils<wa,we,cmp>;
 var
  a:wa;
  b:array[0..2*nmax+1] of wb;
  h,d:array[0..nmax+1] of longint;
  i,j,k,p,n,x:longint;
  put,ti:longint;
  tj:pwb;
 procedure hahainc(x,y:longint);
  begin
   inc(put);
   b[put].n:=@b[h[x]];
   b[put].t:=y;
   h[x]:=put;
  end;
 function cmp.c(x,y:we):boolean;inline;static;
  begin
   exit(x.d>y.d);
  end;
 begin
  readln(n);
  setlength(a,n);
  filldword(h,sizeof(h)>>2,0);
  for i:=0 to n-1 do
   begin
    read(a[i].d);
    a[i].i:=i+1;
    for j:=1 to a[i].d do
     begin
      read(k);
      hahainc(i+1,k);
      hahainc(k,i+1);  //加了双向边,用于后面判断用.
     end;
    readln;
   end;
  sort.sort(a,n);      //按照打败的人数排序.
  filldword(d,sizeof(d)>>2,0);
  x:=0;
  for i:=0 to n-1 do
   begin
    d[a[i].i]:=1;
    inc(x,a[i].d);      //x即为统计sum, 先将人数累加.
    tj:=@b[h[a[i].i]];
    while tj<>@b[0] do  //遍历所有胜负关系.
     begin
      ti:=tj^.t;
      if d[ti]=1 then dec(x);  //如果能打败的是集合S中的人, 或者被集合S中的人打败(双向边), 则将人数减少.
      tj:=tj^.n;
     end;
    if x=(i+1)*(n-i-1) then break;  //满足条件即可退出.
   end;
  p:=0;
  for i:=1 to n do
   if d[i]=1 then
    begin
     inc(p);
     d[p]:=i;
    end;
  write(p,' ');
  for i:=1 to p-1 do
   write(d[i],' ');
  writeln(d[p]);
 end.
