int main(){
    scanf("%lld%lld",&n,&m);
    if(m>n) swap(n,m);
    w=n-m;
    jud=(int)(((sqrt(5.0)+1.0)/2.0)*w);// 向下取整 
    if(jud==m) printf("0");
    else printf("1");
    return 0;
