long long ans = 0; 

inline int read()
{
	int x = 0, w = 1; char c = getchar();
	while(c < '0' || c > '9') { if (c == '-') w = -1; c = getchar(); }
	while(c >= '0' && c <= '9') { x = x * 10 + c - '0'; c = getchar(); }
	return x * w;
}

int main()
{
	n = read(); k = read();
	for(int r, l = 1; l <= n; )
	{
		int t = k / l; r = t ? min(k / t, n) : n;
		ans += 1ll * (r - l + 1) * k - 1ll * (r + l) * (r - l + 1) * t / 2; l = r + 1; 
	}
	printf("%lld\n", ans); 
	return 0;
}

