#include<bits/stdc++.h>
using namespace std;
int main()
{
    long long n, k, ans = 0;
    long long left = 1, right, rest;
    scanf("%lld%lld", &n, &k);
    while (left <= n && left <= k) //求从1到min(n, k)内的总和
    {
        right = min(k / (k / left), n);	
        rest = k % left;
        ans += (rest + rest - (right - left) * (k / left)) * (right - left + 1) / 2; #同一分块内是公差为 k / left 的等差数列
        left = right + 1;
    }
    if (n > k)
    {
        ans += k * (n - k);
    }
    cout << ans;
}
