	scanf("%lld%lld", &n, &k);
	if(n > k) {
		ans += (n - k) * k;
		n = k;
	}
	ans += n * k;
	for(ll i = 1, r = 0; i <= n; i = r + 1) {
		r = min(k / (k / i), n);
		ans -= (r - i + 1) * (r + i) * (k / i) / 2;
	}
	printf("%lld\n", ans);
	return 0;
}
