```cpp

#include<iostream>
#include<math.h>
using namespace std;
long long n,k,i,answer=0,j; 
int main ()
{
    cin>>n>>k;
    for (i=2;(double)k/i>=floor(sqrt(k))&&n>k/i;i++)
    answer+=(min(k/(i-1),n)-k/i)*(k%min(n,k/(i-1))+k%(k/i+1))/2;
    if (n>k)
    answer+=k*(n-k);
    for (j=1;j<=floor(k/(i-1))&&j<=n;j++)
    answer+=k%j;
    cout<<answer;
    return 0;
} 

```
