   if a[i]<>a[i-1] then
    begin
     c[i]:=c[i-1]+1;
     ls[c[i]]:=a[i];
    end
   else
    c[i]:=c[i-1];
  a:=c;
  sort2(1,n);
  maxh:=500000;
  for i:=1 to k-1 do
   update(1,1,maxh,a[i],1);
