            for(; sent[i] != '.'&&sent[i] != ','&&sent[i] != ' '; i++) p += sent[i];
            i--;    //需要再减到空格、句点所在的位置 
            for(int j = 0; j < n; j++){
                if(change(p) == change(s[j])&&!visited[j]){ val++; visited[j] = -1; break; }
            }
        }
    }
    cout << val << endl;
    return 0;
} 
