#include <iostream>
#include <string>
#include <cstring>
using namespace std;
#define HASH_SIZE 1000007
struct SHashNode {
    string strData; int iSen;
    SHashNode * pNext;
} * arrHash[HASH_SIZE];
void AddHash(string str)
{
    int iHash = 0; int iStrLen = strlen(str.c_str());
    for (int i = 0; i < iStrLen; i++)
        str[i] = tolower(str[i]);
    for (int i = 1; i <= iStrLen; i++)
    {
        iHash = iHash * 26 + str[i - 1] - 'a';
        iHash %= HASH_SIZE;
    }
    if (iHash < 0)
        iHash = -iHash;
    SHashNode * pTmp = arrHash[iHash];
    while (pTmp)
    {
        if (pTmp->strData == str)
            return;
        pTmp = pTmp->pNext;
    }
    pTmp = new SHashNode;
    pTmp->strData = str;
    pTmp->iSen = 0;
    pTmp->pNext = arrHash[iHash];
    arrHash[iHash] = pTmp;
}
bool FindHash(string str, int iSen)
{
    int iHash = 0; int iStrLen = strlen(str.c_str());
    for (int i = 0; i < iStrLen; i++)
        str[i] = tolower(str[i]);
    for (int i = 1; i <= iStrLen; i++)
    {
        iHash = iHash * 26 + str[i - 1] - 'a';
        iHash %= HASH_SIZE;
    }
    if (iHash < 0)
        iHash = -iHash;
    SHashNode * pTmp = arrHash[iHash];
    while (pTmp)
    {
        if (pTmp->strData == str)
        {
            bool bAns = (iSen != pTmp->iSen);
            pTmp->iSen = iSen;
            return bAns;
        }
        pTmp = pTmp->pNext;
    }
    return false;
}
int iWordTot;
string strTmp;
int iAns;
int main()
{
    cin >> iWordTot;
    for (int i = 1; i <= iWordTot; i++)
    {
        cin >> strTmp;
        AddHash(strTmp);
    }
    int iSen = 1, iStrLen = 0;
    string str;
    while (cin >> strTmp)
    {
        iStrLen = strlen(strTmp.c_str());
        for (int i = 1; i <= iStrLen + 1; i++)
        {
            if (i != iStrLen + 1 && strTmp[i - 1] != ',' && strTmp[i - 1] != '.')
                str += strTmp[i - 1];
            else
            {
                iAns += FindHash(str, iSen);
                str = "";
            }
            if (strTmp[i - 1] == '.')
                iSen++;
        }
    }
    cout << iAns;
    return 0;
}
