#include <cstdio>
#include <iostream>
#include <string>
#include <algorithm>
#include <set>

using namespace std;

set <string> key, cnt; 

int main(void) {
    int n, ans = 0;
    char c;
    scanf("%d", &n);
    for (int k = 0; k < n; k++) {
        string s;
        cin >> s;
        transform(s.begin(), s.end(), s.begin(), ::toupper);
        key.insert(s);
    }
    string word;
    while (scanf("%c", &c) == 1) {
        if (c >= 'a' && c <= 'z') {
            c += 'A' - 'a';
        }
        if (c >= 'A' && c <= 'Z') {
            word.push_back(c);
        } else {
            if (!word.empty()) {
                if (key.find(word) != key.end()) {
                    cnt.insert(word);
                }
                word.clear();
            }
            if (c == '.') {
                ans += cnt.size();
                cnt.clear();
            }
        }
    }
    printf("%d", ans);
    return 0;
}
