#include <bits/stdc++.h>
using namespace std;
#define MAXN 1000
struct Edge
{
    int to,next;
}e[MAXN+10];
int head[MAXN+10];
void addEdge(int from,int to)
{
    static int c=0;
    e[++c]=(Edge){to,head[from]};
    head[from]=c;
}
int fa[MAXN+10];
bool vis[MAXN+10];
int deep[MAXN+10];
void dfs2(int now,int d)
{
    vis[now]=false;
    if (d==0) return;
    for(int i=head[now]; i!=0; i=e[i].next)
    {
        dfs2(e[i].to,d-1);
    }
    dfs2(fa[now],d-1);
}
