	for (int i=head[u];i;i=e[i].next){
		int v=e[i].link;
		if (v!=fa){
			dfs(v,u);
		}
	}
	int temp1=inf,temp2=inf;;
	for (int i=head[u];i;i=e[i].next){
		int v=e[i].link;
		if (v!=fa){
			dp[u][0]+=f[v][4];
			dp[u][1]+=f[v][3];
			dp[u][2]+=f[v][2];
			temp1=min(temp1,dp[v][0]-f[v][3]);
			temp2=min(temp2,dp[v][1]-f[v][2]);
			dp[u][3]+=f[v][2];
			dp[u][4]+=f[v][3];
		}
	}
	dp[u][0]++;
	dp[u][1]+=temp1;
	dp[u][2]+=temp2;
	f[u][0]=dp[u][0];
	for (int i=1;i<5;i++){
		f[u][i]=min(dp[u][i],f[u][i-1]);
	}
}
