#define ll long long
const int MAXN = 5e5 + 7;
const int INF = 2.1e9;
const int mod = 1e9 + 7;

int n;
vector<int> a[1017];
int depth[1017];
int fa[1017];
bool ok[1017];

struct node{
    int id;
    node(int x){id = x;}
    node(){}
};

bool operator < (node n1, node n2){
    return depth[n1.id] < depth[n2.id];
}

priority_queue<node> q;

int main(){
    cin >> n;
    for(int i = 2; i <= n; i++){
        int x;
        cin >> x;
        a[x].push_back(i);
        a[i].push_back(x);
    }
    stack<int> s;
    s.push(1);
    depth[1] = 1;
    fa[1] = 1;
    while(!s.empty()){
        int top = s.top();
        s.pop();
        for(int i = 0; i < a[top].size(); i++){
            int nxt = a[top][i];
            if(depth[nxt]) continue;
            depth[nxt] = depth[top] + 1;
            fa[nxt] = top;
            s.push(nxt);
        }
    }
    for(int i = 1; i <= n; i++)
        q.push(node(i));
    int ans = 0;
    while(!q.empty()){
        int top = q.top().id;
        q.pop();
        if(ok[top]) continue;
        int grandfa = fa[fa[top]];
        ans ++;
        for(int i = 0; i < a[grandfa].size(); i++){
            int son = a[grandfa][i];
            ok[son] = 1;
            for(int j = 0; j < a[son].size(); j++){
                int gc = a[son][j];
                ok[gc] = 1;
            }
        }
    }
    cout << ans << endl;
}

