		a[i].fa=x;
		sor[i].now=i;
		sor[i].d=a[i].d;
	}
	sort(sor+1,sor+n+1,cmp);
	int ans=0;
	for(int j=1;j<=n;j++){
		int i=sor[j].now;
		if(a[a[i].fa].ok!=2&&!a[i].ok&&!a[i].vis&&!a[a[i].fa].vis&&!a[a[a[i].fa].fa].vis){
			ans++;
			if(a[a[i].fa].fa){
				a[a[a[i].fa].fa].vis=1;
				a[a[a[a[a[i].fa].fa].fa].fa].ok=1;
				a[a[a[a[i].fa].fa].fa].ok=2;
			}
			else if(a[i].fa){
				a[a[i].fa].vis=1;
				a[a[a[i].fa].fa].ok=2;
			}
			else a[i].vis=1;
		}
	}
