        dp[x+1][y+1]=z;
    }
    for(int i=1;i<=5001;i++)
        for(int j=1;j<=5001;j++)
            dp[i][j]=dp[i-1][j]+dp[i][j-1]-dp[i-1][j-1]+dp[i][j];
    for(int i=0;i<=5001-r;i++)
        for(int j=0;j<=5001-r;j++)
