	if (rson[u]) 
		if (!v[u]) v[u]=v[rson[u]],id[u]=id[rson[u]];
			else if (v[rson[u]]<v[u]||v[rson[u]]==v[u]&&id[rson[u]]>id[u]) v[u]=v[rson[u]],id[u]=id[rson[u]];
