		sum[i]=read();
		sum[i]+=sum[i-1];
	}
	for(int i=1;i<=n;++i){
		while(l<=r&&sum[i-1]+pre[i-1]<=q[r])r--;
		q[++r]=sum[i-1]+pre[i-1];
		num[r]=i-1;
		while(l<r&&sum[i]>=q[l+1])l++;
		f[i]=f[num[l]]-num[l]+i-1;
		pre[i]=sum[i]-sum[num[l]];
	}
	print(f[n]);
	return 0;
}
