const int N = 105;
const int P[] = {30071, 10007, 10917};
int n, m;
long long cnt = 0, ans[1000005];
long long a[N][3];
bool v[100000][3];

inline bool calc(int x, int y){
    int i, j, k;
    long long temp = a[n][y];
    for(i = n - 1; i >= 0; i--){
        temp = ((temp * x + a[i][y]) % P[y] + P[y]) % P[y];
    }
    //printf("x = %d y = %d temp = %d\n", x, y, temp);
    return temp == 0;
}

inline void init(){
    bool sign;
    int i, j, k;
    char s[100005];
    cin >> n >> m;
    for(i = 0; i <= n; i++){
        cin >> s;
        int len = strlen(s);
        if(s[0] == '-'){
            j = 1;
            sign = 1;
        }
        else {
            j = 0;
            sign = 0;
        }
        for(; j < len; j++){
            for(k = 0; k <= 2; k++){
                a[i][k] = (a[i][k] * 10 + s[j] - '0') % P[k];
            }
        }
        if(sign)
            for(j = 0; j <= 2; j++){
            	a[i][j] = P[j] - a[i][j];
            }
    }
}

int main() {
    init();
    int i, j, k;
    for(i = 0; i <= 2; i++){
    	for(j = 0; j <= P[i]; j++){
    		v[j][i] = calc(j, i);
		}
	}
    for(i = 1; i <= m; i++){
        if(v[i % P[0]][0] && v[i % P[1]][1] && v[i % P[2]][2] ){
            ans[++cnt] = i;
        }
    }
    cout << cnt << endl;
    for(i = 1; i <= cnt; i++){
        cout << ans[i] << endl;
    }
    return 0;
}
