    for(int i = 1; i <= m; ++i) {
        cin >> x[i] >> y[i];
    }
    for(int i = 1; i <= m; ++i) {
        for(int j = 1; j <= rcnt; ++j) {
            if(x[i] > min(rx1[j], rx2[j]) && x[i] < max(rx1[j], rx2[j]) && y[i] > min(ry1[j], ry2[j]) && y[i] < max(ry1[j], ry2[j])) {
                ++ans[i];
            }
        }
        for(int j = 1; j <= ccnt; ++j) {
            if((cx[j] - x[i]) * (cx[j] - x[i]) + (cy[j] - y[i]) * (cy[j] - y[i]) < cr[j] * cr[j]) {
                ++ans[i];
            }
        }
    }
    for(int i = 1; i <= m; ++i) {
        cout << ans[i] << endl;
    }
    return 0;
}
