	scanf("%d%d",&n,&m);
	for(int i = 1;i <= n;++ i)
	{
		cin >> a[i].acioi;
		if(a[i].acioi == 'r')
			cin >> a[i].x1 >> a[i].y1 >> a[i].x2 >> a[i].y2;
		else
			cin >> a[i].x1 >> a[i].y1 >> a[i].x2;
	}
	double x,y;
	for(int i = 1;i <= m;++ i)
	{
		cin >> x >> y;
		int js = 0;
		for(int j = 1;j <= n;++ j)
		{
			if(a[j].acioi == 'r')
			{
				if((a[j].x1 > x && a[j].y1 > y && a[j].x2 < x && a[j].y2 < y) || (a[j].x1 < x && a[j].y1 < y && a[j].x2 > x && a[j].y2 > y) )
					js ++;
			}
			else
			{
				if( sqrt((a[j].x1 - x) * (a[j].x1 - x) + (a[j].y1 - y) * (a[j].y1 - y)) < a[j].x2)
					js ++;
			}
		}
		cout << js << endl;
