        if(f[n]-xz[n]-xz[n-1]!=0) bz=true; 
        if(bz==false) ans++;
        xz[1]=1;
        xz[2]=0;
        for(int i=3;i<=n;i++)
        {
            xz[i]=(f[i-1]-xz[i-1]-xz[i-2]);
