        return 0;
    } //开头结尾一定不可能是3 
    for(rig i = 1; i <= N; i++)
        if(num[i] == 3)
            Boom[i] = Boom[i - 1] = Boom[i + 1] = 1;
    dfs(1); 
    print(ans);
    return 0;
}
