int n;
bool check()
{
    for(int i=2;i<=n;i++)
     {
        dp[i][0]=dp[i-1][1]-dp[i-1][0]-dp[i-2][0];
        if((dp[i][0]!=0)&&(dp[i][0]!=1))
         return 0;
        if(i==n&&(dp[i][0]+dp[i-1][0])!=dp[i][1])
          return 0; 
     }
    return 1;
}
int main()
{
    scanf("%d",&n);
    for(int i=1;i<=n;i++)
     scanf("%d",&dp[i][1]);
    int ans=0;
    dp[1][0]=1;
    if(check()) ans++;
    dp[1][0]=0;
    if(check()) ans++;
    printf("%d",ans);
    return 0;
}
