#define N 10004//稍微加快
using namespace std;
int n,a[N];//number
int b[N];//mine
bool judge(){
    b[0]=0;
    for (int i=1;i<=n;++i){//++i个人习惯理论上快一点点
        b[i+1]=a[i]-b[i-1]-b[i];
        if (b[i+1]>1||b[i+1]<0) return 0;
    }
    return b[n+1]==0;
}
int main(){
    scanf("%d",&n);
    for (int i=1;i<=n;++i) scanf("%d",&a[i]);
    int ans=0;
    for (b[1]=0;b[1]<2;++b[1])
        ans+=judge();
    printf("%d\n",ans);
    return 0;
}
