#include<bits/stdc++.h>
using namespace std;
#define ll long long
const int mod=1000000007;
const int M=55,N=1005;
int m,n;
ll a[M],b[N],sum[N],tot,waste,mid;
bool dfs(int x,int lst)
{
	if(tot-waste<sum[mid]) return false;
	if(x==0) return 1;
	bool ok=0;
	for(int i=lst;i<=n;i++)
	{
		if(a[i]>=b[x])
		{
			a[i]-=b[x];
			if(a[i]<b[1]) waste+=a[i];
			if(b[x-1]==b[x]) ok=dfs(x-1,i);
			else ok=dfs(x-1,1);
			if(a[i]<b[1]) waste-=a[i];
			a[i]+=b[x];
			if(ok) return true;
		}
	}
	return false;
}
int main()
{
	scanf("%d",&m);
	for(int i=1;i<=m;i++)
	{
		scanf("%lld",&a[i]);
		tot+=a[i];
	}
//	sort(a+1,a+m+1);
	scanf("%d",&n);
	for(int i=1;i<=n;i++)
	{
		scanf("%lld",&b[i]);
	}
	sort(b+1,b+n+1);
	for(int i=1;i<=n;i++) sum[i]=sum[i-1]+b[i];
	while(n&&sum[n]>tot) n--;
	ll L=0,R=n;
	while(L<=R)
	{
		waste=0;
		mid=(L+R)>>1;
		if(dfs(mid,1)) L=mid+1;
		else R=mid-1;
	}
	printf("%lld",R);
	return 0;
}
