```
//逆推
# include <iostream>
# include <cstdio>
# include <algorithm>

using namespace std ;

const int NR = 105 ;

int n , m , t ;
int dp1[NR][15][2] ;
int dp2[NR][15][5] ;
int a[NR][3] ;
int val2[5][5] = 
{
	{ 0 , 1 , 1 , 1 , 2 } ,
	{ 0 , 0 , 1 , 1 , 1 } ,
	{ 0 , 1 , 0 , 1 , 1 } ,
	{ 0 , 1 , 1 , 0 , 2 } ,
	{ 0 , 0 , 0 , 1 , 0 } 
} ;

void do1()
{
	for ( int j = 0 ; j < 15 ; j++ )
		dp1[0][j][1] = -1e9 ;
	for ( int j = 0 ; j < 15 ; j++ )
		dp1[0][j][0] = 0 ;

	for ( int i = 1 ; i <= n ; i++ )
		for ( int j = 0 ; j <= t ; j++ )
			for ( int k = 0 ; k <= 1 ; k++ )
			{
				dp1[i][j][k] = -1e9 ;
				if ( j - k >= 0 )
					dp1[i][j][k] = dp1[i - 1][j - k][0] ;
				dp1[i][j][k] = max( dp1[i][j][k] , dp1[i - 1][j][1] ) ;
				if ( k )
					dp1[i][j][k] += a[i][1] ;
			}
	printf( "%d\n" , max( dp1[n][t][0] , dp1[n][t][1] ) ) ;
}

int calc( int p , int q )
{
	int ans = 0 ;
	if ( q == 1 || q == 3 || q == 4 )
		ans += a[p][1] ;
	if ( q == 2 || q == 3 || q == 4 )
		ans += a[p][2] ;
	return ans ;
}

void do2()
{
	for ( int i = 0 ; i <= n ; i++ )
		for ( int j = 0 ; j <= t ; j++ )
			for ( int k = 0 ; k <= 4 ; k++ )
				dp2[i][j][k] = -1e9 ;
	for ( int j = 0 ; j < 15 ; j++ )
		dp2[0][j][0] = 0 ;

	for ( int i = 1 ; i <= n ; i++ )
		for ( int j = 0 ; j <= t ; j++ )
		{
			for ( int k = 0 ; k <= 4 ; k++ )
			{
				for ( int w = 0 ; w <= 4 ; w++ )
				{
					if ( j >= val2[w][k] )
					{
						dp2[i][j][k] = max( dp2[i][j][k] , 
						dp2[i - 1][j - val2[w][k]][w] ) ;
					}
				}
				if ( k == 1 )
					dp2[i][j][k] += a[i][1] ;
				if ( k == 2 )
					dp2[i][j][k] += a[i][2] ;
				if ( k == 3 || k == 4 )	
					dp2[i][j][k] += a[i][1] + a[i][2] ;
			}
		}
	int ans = -1e9 ;
	for ( int i = 0 ; i <= 4 ; i++ )
		ans = max( ans , dp2[n][t][i] ) ;
	printf("%d\n" , ans) ;
}

int main()
{
	scanf( "%d%d%d" , &n , &m , &t ) ;
	for ( int i = 1 ; i <= n ; i++ )
	{
		for ( int j = 1 ; j <= m ; j++ )
		{
			scanf( "%d" , &a[i][j] ) ;
		}
	}
	if ( m == 1 )
	{
		do1() ;
	}
	else
	{
		do2() ;
	}
	return 0 ;
}
