{
    inline void read(int &n)
    {
        int sum=0;
        bool pn=0;
        char c=getchar();
        while(c<'0'||c>'9')
        {
            if(c=='-') pn=1;
            c=getchar();
        }
        while(c<='9'&&c>='0')
        {
            sum=sum*10+c-'0';
            c=getchar();
        }
        n=pn? ~sum+1 : sum;
    }
    inline void put(int x)
    {
        int y=10,len=1;  
        while(y<=x)  {y*=10;len++;}  
        while(len--)
        {
            y/=10;
            putchar(x/y+48);
            x%=y;
        }  
