#define ri register int
#define il inline

const int INF=0x3f3f3f3f,N=1.5e6,C=19940105;
int n;
int u[N],size[N],sum[N];

il ll read(){
    ll x=0,y=1;
    char c=getchar();
    while(c<'0'||c>'9'){
