}

signed main(){
    n=read();
    for(ri i=2;i<=n;i++)
        u[i]=read();
    for(ri i=1;i<=10;i++){
        printf("Case #%d:\n1\n",i);
        if(i!=1)
            for(ri i=2;i<=n;i++)
		    	u[i]=(u[i]+C)%(i-1)+1;
        for(ri i=1;i<=n;i++){
	    	size[i]=1;
	    	sum[i]=0;	
	    } 
	    for(ri i=n;i>1;i--)
	    	size[u[i]]+=size[i];
	    for(ri i=1;i<=n;i++)
	    	sum[size[i]]++;
        for(ri i=2;i<=n;i++){
			int mid=0;
			if(!(n%i)){
				for(ri j=1;i*j<=n;j++)
					mid+=sum[i*j];
				if(mid*i>=n)
                    printf("%d\n",i);
			}
		}
    }
    return 0;
