#include<string>
#define maxn 250000
#define ll long long
using namespace std;
ll n,tot,a[maxn],b[maxn],c[maxn];
ll d[maxn],s[maxn];
bool cmp(int x,int y)
{
    return a[x]<a[y];
}
int main()
{
   //freopen("book.in","r",stdin);
//freopen("book.out","w",stdout);
   scanf("%lld",&n);
    for(int i=1;i<=n;i++)
        scanf("%lld%lld",&a[i],&a[n+i]);
    for(int i=1;i<=2*n;i++)
        c[i]=i;
    sort(c+1,c+2*n+1,cmp);
    tot=0;
    for(int i=1;i<=2*n;i++)
    {
        if(i==1||a[c[i]]!=a[c[i-1]])
            tot++;
        b[c[i]]=tot;
        d[tot]=a[c[i]];
    }
    for(int i=1;i<=n;i++)
    {
        s[b[i]]++;
        s[b[n+i]+1]--;
    }
    for(int i=1;i<=tot;i++)
        s[i]+=s[i-1];
    int ans=0;
    for(int i=1;i<=tot;i++)
        if(s[i]*d[i]>d[ans]*s[ans])
            ans=i;
    printf("%lld\n",d[ans]*s[ans]);
    return 0;
}
