最后贴个~~高清~~无注释代码：
``` cpp
#include<bits/stdc++.h>
using namespace std;
const int maxn=5005;
long long a,b,c,d;
int q,m,n,u,v,cnt;
int x[maxn*maxn],t[maxn*maxn];
struct node{
	int l,r;
}e[maxn];
void init(){
	for(int i=1;i<=n*m;++i){
		x[i]=(a*x[i-1]*x[i-1]+b*x[i-1]+c)%d;
		t[i]=i;
	}
	for(int i=1;i<=n*m;++i)
		swap(t[i],t[x[i]%i+1]);
	while(q--){
		scanf("%d%d",&u,&v);
		swap(t[u],t[v]);
	}
	for(int i=1;i<=n*m;++i)
		x[t[i]]=i;
	for(int i=1;i<=n;++i)
		e[i].l=1,e[i].r=m;
}
void work(){
	for(int i=1;i<=n*m;++i){
		int nx,ny;
		if(x[i]%m==0) nx=x[i]/m;
		else nx=x[i]/m+1;
		ny=x[i]%m;
		if(!ny) ny=m;
		if(ny>=e[nx].l&&ny<=e[nx].r){
			++cnt;
			printf("%d ",i);
			if(cnt==n+m-1) return;
			for(int j=1;j<=n;j++){
				if(j<nx) e[j].r=min(e[j].r,ny);
				else if(j>nx) e[j].l=max(e[j].l,ny);
			}	
		}
	}
}
signed main(){
	scanf("%d%lld%lld%lld%lld%d%d%d",&x[0],&a,&b,&c,&d,&n,&m,&q);
