					sum[aa]+=a[k][j];   //统计所在列获得的分数 
				}
				for(int k=0;k<n;k++){
					sum[aa]+=a[i][k];   //统计所在行获得的分数 
				}
				aa++;      //统计完一个点的分数后使用数组的下一位进行存储 
			}
		}
