		push_down(now,l,r);
		LL mid = (l + r) >> 1;
		if(x <= mid) ans += look_up(now * 2,l,mid,x,y);
		if(y > mid) ans += look_up(now * 2 + 1,mid + 1,r,x,y);
	}
