```cpp
#include <iostream>
#include <cmath>
using namespace std;

const int Mod = 1e9 + 9;

int n, f[10010][11][11], ans;

bool is_prime(int x) {
	if (x == 0 || x == 1) return false;
	for (int i=2; i<=sqrt(x); i++)
		if (x % i == 0) return false;
	return true;
}

int main() {
	cin >> n;
	for (int i=1; i<=9; i++)
		for (int j=0; j<=9; j++)
			f[2][i][j] = 1;
	for (int i=3; i<=n; i++)
		for (int j=0; j<=9; j++)
			for (int k=0; k<=9; k++)
				for (int l=1; l<=9; l++)
					if (is_prime(l*100 + j*10 + k))	f[i][j][k] = (f[i][j][k] + f[i-1][l][j]) % Mod;
	for (int i=0; i<=9; i++)
		for (int j=0; j<=9; j++)
			ans = (ans + f[n][i][j]) % Mod;
	cout << ans << endl;
	return 0;
}
```

