#include<cstdio>
#include<cstring>
#include<algorithm>
#define N 420000
using namespace std;
int n,m,a[N],b[N],c[N],ans;
int main(){
    scanf("%d",&n);
    for(int i=1;i<=n;++i){
        scanf("%d",&m);
        memset(a,0,sizeof(a));
        memset(c,0,sizeof(c));
        scanf("%d",&a[1]);
        for(int i=1;i<=m;++i){
            b[i]=1;
            c[i]=1;
        }
        for(int j=2;j<=m;++j){
            scanf("%d",&a[j]);
            for(int k=j-1;k>=1;k--)
                if(a[j]>=a[k]){
                    if(b[j]<b[k]+1){
                        b[j]=b[k]+1;
                        c[j]=c[k];
                    }
                    else
                        if(b[j]==b[k]+1)
                            c[j]++;
                }
        }
        int mmax=-1;
        for(int j=1;j<=m;++j){
            if(b[j]==mmax)
                ans+=c[j];
            else{
                if(b[j]>mmax){
                    ans=c[j];
                    mmax=b[j];
                }
            }
        }
        printf("%d %d\n",mmax,ans);
    }
    return 0;
}
