	cin>>s1>>s2>>s3;
	s1=" "+s1;L1=s1.length()-1;
	s2=" "+s2;L2=s2.length()-1;
	s3=" "+s3;L3=s3.length()-1;//加上空格为了在状态转移的过程中防止下标越界 
	for (int i=1;i<=L1;i++)
